","keccak256":"0xf92515413956f529d95977adc9b0567d583c6203fc31ab1c23824c35187e3ddc","license":"MIT"},"contracts/ContributionPool.sol":{"content":"// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.9;

 import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Strings.sol";


import "./DAOToken.sol";
import "./Registration.sol";

/// This contract should handle the relation between open source project creator and open source project contributors.
/// personas : 
    //// 1- Open source project contributors , looking for open source project to contribute to and get paid for thier work . with each contribution DAO token is minted 
    //// 2- Open source project creator , look for contributors to thier open source project and pay them for thier work . They have to be DAO token holder to be able to list thier project and they have to deposit bug bounty amount in the contract to be able to pay contributors
/// The core functionality is to enable  Creators to list thier github repos, set how the deposit that they will be paid , once the contributor create PR and close the issue , the contract release the payment to the contributor and mint DAO token to the contributor
contract ContributionPool  is  Ownable{
     uint256 assignedReqId;
     uint256 counter;
    Registration registration;
    DAOToken token;
    mapping (address=>Listing) CreatorListings;
    mapping (string=>address) public githubToCreatorAddress;
    struct Listing{
        uint256 listingId;
        string githubRepo;
        uint256 currentDeposit;
        uint256 payableAmount;
            }         
  mapping(uint256 => address) internal runningClaims;
  mapping(uint256 => address) internal requestIdToCreator;
  mapping(uint256 => string) internal requestIdToprUrl;
  mapping(uint256 => uint256) internal requestIdToListingId;

  mapping (string=>bool) public usedRequests;


   event Claimed(uint amount, address by);
   event Requested(uint amount, address by, string prUrl);

  constructor( address _registration, address _token )  {
     registration = Registration(_registration);
    token = DAOToken(_token);

  }
    function listRepo (string memory githubRepo, uint256 payableAmount) public payable {
        // require(Registration(msg.sender).IsProjectCreatorRegistered(msg.sender),"not registered as project creator");
        // require(CreatorListings[msg.sender].listingId==0,"already listed");
        counter++;
        githubToCreatorAddress[githubRepo] = msg.sender;
        CreatorListings[msg.sender] = Listing(counter,githubRepo,msg.value,payableAmount);
    }


  /// @notice Can be called by contributor to request fund
  function claim(string calldata repo, string  calldata prUrl ) public {
    ///  check balance to make sure that project creators don't sepend more than the deposit
    require(CreatorListings[githubToCreatorAddress[repo]].currentDeposit >= CreatorListings[githubToCreatorAddress[repo]].payableAmount,"not enough balance to pay");
    require(!usedRequests[prUrl],"request already used");  
    assignedReqId++;
     requestIdToCreator[assignedReqId] = githubToCreatorAddress[repo];
     runningClaims[assignedReqId] = githubToCreatorAddress[repo];
    requestIdToprUrl[assignedReqId] = prUrl;
    requestIdToListingId[assignedReqId] = CreatorListings[githubToCreatorAddress[repo]].listingId;
  }


     /// @notice Finalizes the claim process by the creator to fun the contributor
  function finalizeClaim(string memory _login, string memory githubIssue, uint256 bountyAmount ) public {
// get address of github handle
    address devAddress= registration.getDeveloperAddress(_login);
    usedRequests[githubIssue] = true;
// make the github issue as done 
 CreatorListings[msg.sender].currentDeposit -= bountyAmount;
// mint token 

    token.mint(devAddress,1);
    // emit event

    emit Claimed(bountyAmount, devAddress);
    // release payment 
    payable(msg.sender).transfer(bountyAmount);
  }







    event ListingCreated(uint256 listingId, string githubRepo, uint256 deposit, uint256 payableAmount);


}","keccak256":"0x6f9bde2641468da7ed2990c17ffcbaf1f53ffc624fbc79dead749bf46839a938","license":"UNLICENSED"},"contracts/DAOToken.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Permit.sol";

contract DAOToken is ERC20, AccessControl, ERC20Permit {
    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");

    constructor(address defaultAdmin, address minter)
        ERC20("DAOToken", "DT")
        ERC20Permit("DAOToken")
    {
        _grantRole(DEFAULT_ADMIN_ROLE, defaultAdmin);
        _grantRole(MINTER_ROLE, minter);
    }

    function mint(address to, uint256 amount) public onlyRole(MINTER_ROLE) {
        _mint(to, amount);
    }
}
","keccak256":"0xae688151088b937373ecd342604ae70748ee6b7d3d262ae158ee1d765b2af291","license":"MIT"},"contracts/Registration.sol":{"content":"// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.9;
import "@openzeppelin/contracts/security/Pausable.sol";

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

/// the contract should manage user registration , for creator and contributors

contract Registration is Pausable, Ownable {

    mapping (string=>address) public  githubToAddress;
    mapping (address=>string) public addressToGithub;
    mapping (address=>uint256) public creatorRegistratedTime;
    IERC20 daoToken;
    uint256 public blanceToJoin;
    constructor(address token , uint256 _blanceToJoin)  {
        daoToken = IERC20(token);
        require(_blanceToJoin>10,"balance must be greater than 10");
        blanceToJoin = _blanceToJoin;
    }
    function updateBlanceToJoin(uint256 _blanceToJoin) public onlyOwner{
        require(_blanceToJoin>10,"balance must be greater than 10");
        blanceToJoin = _blanceToJoin;
    }

        // register as contributor

    function joinAsContributor(string memory githubUsername) public  whenNotPaused{
        require(githubToAddress[githubUsername] == address(0), "github username already registered");
        githubToAddress[githubUsername] = msg.sender;
        addressToGithub[msg.sender] = githubUsername;
        emit ContributorRegistered(msg.sender, githubUsername);
    }


    // join as open-source-project-creator
    // later we need to handle case of creator manipulating the contract by selling the token after joining 

    function joinAsOpenSourceProjectCreator() public  whenNotPaused{
        require(creatorRegistratedTime[msg.sender]==0, "already registered as open source project creator");
      /// make sure creator is qualified to join
        require(daoToken.balanceOf(msg.sender) >= blanceToJoin, "not enough balance to join");
        creatorRegistratedTime[msg.sender] = block.timestamp;
        emit CreatorRegistered(msg.sender);
    }

 function IsProjectCreatorRegistered(address creatorAddress) public view returns(bool){
     return creatorRegistratedTime[creatorAddress]!=0;}
    // leave 
    function getDeveloperGithub(address dev) public view returns(
        string memory githubUsername
    ){
        return addressToGithub[dev];
    }
    function getDeveloperAddress(string memory githubUsername) public view returns( address){
        return githubToAddress[githubUsername];
    }


    function pause() public onlyOwner {
        _pause();
    }

    function unpause() public onlyOwner {
        _unpause();
    }

    event CreatorRegistered(address indexed creatorAddress);
    event ContributorRegistered(address indexed contributorAddress, string indexed githubUsername);


}

","keccak256":"0x4d46edd582175fb5f70698ba5e1c2ee354e389755f1c3ce498a3598609aa5ed7","license":"UNLICENSED"}},"version":1}
